// 1.按照age 进行排序
// 2.如果age相等，比较name

//实现Comparable接口
public class Person implements Comparable {

    private String name;
    private int age;

    //get/set
    //构造方法
    //toString

    /**
     * 使用该方法进行排序
     */
    @Override
    public int compareTo(Person o) {
        // 安装年龄进行排序
        int result = this.age - o.age;
        System.out.println("比较结果------->:" + result);
        // 如果年龄相同；看成是同一个对象
        if (result == 0) {
            result = this.name.compareTo(o.name); // String的自然排序 去比较
        }
        return result;
    }
}

public static void main(String[] args) {

        // 1.按照age 进行排序
        // 2.如果age相等，比较name
        Person zhangsan = new Person("b", 21);
        Person lisi = new Person("a", 21);
        Person zhangsan2 = new Person("zhangsan", 22);
        Person wangwu = new Person("wangwu", 19);
        Person zhaoliu = new Person("zhaoliu", 25);

        Set set = new TreeSet<>();
        set.add(zhaoliu);
        set.add(zhangsan);
        set.add(lisi);
        set.add(zhangsan2);
        set.add(wangwu);

        System.out.println(set);
    }

//结果
[Person [name=wangwu, age=19], Person [name=a, age=21], Person [name=b, age=21], Person [name=zhangsan, age=22], Person [name=zhaoliu, age=25]]

2.自定义比较器

/**
 * 创建比较器
 * @author THINK
 *
 */
public class MyComparator implements Comparator {

    @Override
    public int compare(Animal o1, Animal o2) {

        // 比较年龄
        int result = o1.getAge() - o2.getAge();
        if (result == 0) {
            result = o1.getName().compareTo(o2.getName());
        }

        return result;
    }

}
//使用
public class Demo06 {
    public static void main(String[] args) {

        // Collections.sort(list, c);
        // 创建自定义比较器
        MyComparator comparator = new MyComparator();
        Set treeSet = new TreeSet<>(comparator);//构造对象，指定比较器

        Animal tiger = new Animal("m", 5);
        Animal dog = new Animal("dog", 8);
        Animal cat = new Animal("cat", 9);
        Animal pig = new Animal("a", 5);
        treeSet.add(dog);
        treeSet.add(cat);
        treeSet.add(pig);
        treeSet.add(tiger);
        System.out.println(treeSet);

        // List也可以使用
        System.out.println("---------------------------------------------------------------");

        List arrayList = new ArrayList<>();
        arrayList.add(dog);
        arrayList.add(cat);
        arrayList.add(pig);
        arrayList.add(tiger);
        System.out.println(arrayList);

        // 指定比较器
        Collections.sort(arrayList, comparator);

        System.out.println("排序之后:" + arrayList);

    }
}

